<div id="msg"></div>

<script type="text/javascript">

	/**
	 * This function remove the newly created device.
	 * The param 'inhibit_login' cannot be used in the registration request because the password is not renewed and we don't have access to it here.
	 * @param accessToken The access token given back by the register request.
	 */
	function requestLogout(accessToken) {
		var req = new XMLHttpRequest();
		var loc = window.location;

		req.open('POST', loc.origin + '/_matrix/client/r0/logout', true);
		if (accessToken) {
			req.setRequestHeader("Authorization", "Bearer " + accessToken);
			req.send(null);
		}
	}

	/**
	 * This function request the registration for the user.
	 */
	function requestRegister() {
		var req = new XMLHttpRequest();
		var msg = document.getElementById('msg');
		var loc = window.location;
		var params = {};
		params.body = generateBody(loc.href);

		req.onreadystatechange = function() {
			if (this.readyState === XMLHttpRequest.DONE) {
				if (this.status === 0) {
					// Nothing here, but we have to do this to prevent early state change without any server response.
					msg.innerHTML = '<div id="loader"></div>';
				} else if (this.status === 200) {
					var res = JSON.parse(this.response);
					msg.innerHTML = '<p>Votre inscription est termin�e.</p><p>Vous pouvez d�s � pr�sent vous connecter en utilisant vos identifiants sur l\'�cran de connexion.</p>';
					requestLogout(res.access_token);
				} else {
					msg.innerHTML = '<p>Une erreur est survenue lors de votre inscription. Merci de r�essayer plus tard.</p>';
				}
			}
		};

		req.open('POST', loc.origin + '/_matrix/client/r0/register', true);
		if (params && params.body) {
			req.setRequestHeader("Content-Type", 'application/json');
			req.send(JSON.stringify(params.body));
		}
	}

	/**
	 * This function return a query string parameter value from an url.
	 * @param url The url to seach on.
	 * @param param The query string parameter requested.
	 * @returns {string} The value of the requested query string parameter
	 */
	function getUrlParam(url, param) {
		var sPart = url.split('?');
		var params = sPart[1].split('&');
		for (var i = 0; i < params.length; i++) {
			var kv = params[i].split('=');
			if (kv[0] === param) {
				return decodeURIComponent(kv[1]);
			}
		}
	}

	/**
	 * This function generate a body for the registration request from the curent url.
	 * @param loc The url containing the parameters.
	 */
	function generateBody(loc) {
		var body = {};
		var threepid_creds = {};
		body.auth = {};
		body.auth.type = 'm.login.email.identity';
		body.auth.session = getUrlParam(loc, 'session_id');
		threepid_creds.sid = getUrlParam(loc, 'sid');
		threepid_creds.client_secret = getUrlParam(loc, 'client_secret');
		body.auth.threepid_creds = threepid_creds;

		return body;
	}

	function update() {
		var strRegister = '#/register';
		var loc = window.location;
		if (loc.hash && loc.hash.substr(0,strRegister.length) === strRegister) {
			requestRegister();
		}
    else {
